import axios from 'axios';
import React, { useEffect, useState } from 'react';

const Test = () => {
    const [apiData, setApiData] = useState([]);
    const storedData = localStorage.getItem("apiData");

    const fetchData =async () => {
        const response = await axios.get("https://randomuser.me/api")
        if (response?.status === 200) {
            localStorage.setItem("apiData", JSON.stringify(response?.data?.results))
            refreshFun()
        }
    }

    const refreshFun = () => {
        if (storedData) {
  try {
      const parsedData = JSON.parse(storedData);
      setApiData(parsedData);
      return parsedData?.length?parsedData:[]
  } catch (error) {
    console.error("Error parsing JSON:", error);
  }
        } else {
            console.log("No data found in localStorage for 'apiData'.");
        }
    }

    useEffect(() => {
        fetchData()
    }, [])

    return (
        <div>
            <button onClick={()=>fetchData()}>Refresh</button>
            <h2>Full Name</h2>
            {apiData?.map((item: any, index) => {

                return (
                    <p key={index+1}>{item?.name?.title} {item?.name?.first} { item?.name?.last}</p>
                )
            })}
        <p>{}</p>
      </div>
    );
}


export default Test;
